// Home
import React, { useState, useEffect } from "react";
// Api.js
import { searchMovie, getPopularMovies } from "../Api.js";
// components
import NavbarDate from "../components/NavbarDate/NavbarDate";
import MoviesList from "../components/MoviesList/MoviesList";
import MovieSlider from "../components/MoviesSlider/MovieSlider";

//MoviesList/MoviesList'
import ForYou from "../utils/BoxForYou";
import BoxPromo from "../utils/BoxPromo";
import BoxMenu from "../utils/BoxMenu";
import BtnHome from "../utils/BtnHome";
import ButtonUp from "../utils/ButtonUp";

//
function Home() {
    const [popularMovies, setPopularMovies] = useState([]);
    useEffect(() => {
        getPopularMovies().then(result => {
            setPopularMovies(result);
            setIsLoading(false);
        });
    }, []);

    const [isLoading, setIsLoading] = useState(true);
    const search = async q => {
        setIsLoading(true);
        const query = await searchMovie(q);
        setPopularMovies(query.results);
        setIsLoading(false);
    };


    // MovieSloser
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const movieList = await getPopularMovies();
                setMovies(movieList);
                setIsLoading(false);
            } catch (error) {
                console.error("Error fetching movies:", error);
            }
        };

        fetchMovies();
    }, []);
    // MovieSliser
    return (
        <main id="home" className="bg-site bg-cover overflow-hidden">
            <NavbarDate />
            <div className="container mx-auto">
                <BoxMenu />
                <BtnHome />
                <ForYou />
                <BoxPromo />
                {isLoading ? (
                    <p>Loading...</p>
                ) : (
                    <MovieSlider movies={movies} />
                )}
                <div className="text-center items-center mb-5">
                    <h2 className="hover:scale-125 font-primary text-accent text-[32px] tracking-[10%] uppercase text-center mt-16">
                        Nonton Film Gratis
                    </h2>
                    <h3 className="font-primary font-semibold text-[26px] leading-[46px] text-center mb-5">
                        Tanpa Karcis
                    </h3>
                    <input
                        className="w-max rounded-2xl px-2 text-black font-bold"
                        placeholder="Cari film kesayangan..."
                        onChange={e => search(e.target.value)}
                    />
                </div>
            </div>
            <MoviesList popularMovies={popularMovies} isLoading={isLoading} />
            <ButtonUp />
        </main>
    );
}
export default Home;
