import { useState } from "react";

function UserProfile(argument) {
    // inisialisasi state dengan objek
    const [user, setUser] = useState({
        nama: "Cecep",
        alamat: "Bong-Bong",
        umur: 35
    });

    // fungsi mengubah nilai Objek state
    const updateUser = () => {
        setUser({
            ...user, // mengambil objek user yg ada
            umur: user.umur + 1 // mengubah nilai umur
        });
    };
    return (
        <div>
            <h1>User Profile</h1>
            <p>Nama = {user.nama}</p>
            <p>Alamat = {user.alamat}</p>
            <p>Umur = {user.umur}</p>
            <button onClick={updateUser}>Rubah Umur</button>
        </div>
    );
}
export default UserProfile;
