import { useState } from "react";
//
function ShowNotifikasi() {
    // Menutup Notifikasi...
    let get = document.getElementById("notifikasi");
    if (get != null) {
        get.style.display = "none";
    } // diPanggil ke <button onClick={ ShowNotifikasi }
    // const [showNotif, setShowNotif] = useState(false);
    //     const closeNotifHandler = () => {
    //         setShowNotif(false);
    //     };
    //     {
    //         showNotif && <ShowNotifikasi onClose={closeNotifHandler} />;
    //     }
    const [show, setShow]= useState(false)

    return (
        <div
            className="fixed z-10 overflow-y-auto top-0 w-full left-0"
            id="notifikasi"
        >{show && <ShowNotifikasi />}
            <div className="fixed inset-x-0 px-4 pb-4 inset-0 flex z-999 items-center justify-center bg-white/10 backdrop-blur">
                <div className="bg-white pt-4 rounded-lg overflow-hidden w-full">
                    <div className="flex items-center flex-col">
                        <div className="flex items-center justify-center h-12 w-12 rounded-full bg-green-100 mx-0 h-10 w-10">
                            <svg
                                className="h-6 w-6 text-green-600"
                                stroke="currentColor"
                                fill="none"
                                viewBox="0 0 24 24"
                            >
                                <path
                                    stroke-linecap="round"
                                    stroke-linejoin="round"
                                    stroke-width="2"
                                    d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"
                                />
                            </svg>
                        </div>
                        <div className="mt-8 p-8 text-left">
                            <h3 className="text-lg font-medium text-gray-900">
                                Cara Login
                            </h3>
                            <div className="mt-2 text-sm w-full">
                                <p>
                                    Pastikan kamu sudah membeli
                                    <b>voucher</b> atau sudah menjadi
                                    <b>member</b>
                                </p>
                                <h4 className="text-md font-bold my-2">
                                    Sudah memiliki voucher?
                                </h4>
                                <p>
                                    Masukkan
                                    <b className="text-green-500">
                                        <a href="voucher">voucher</a>
                                    </b>
                                    yang kamu miliki lalu klik
                                    <b>masuk</b>
                                </p>
                                <h4 className="text-md font-bold my-2">
                                    Sudah menjadi member?
                                </h4>
                                <ol className="list-decimal ml-4">
                                    <li>
                                        Klik
                                        <b>Login Member</b>
                                    </li>
                                    <li>
                                        Masukkan
                                        <b>username</b> dan
                                        <b>password</b> yang kamu miliki lalu
                                        klik
                                        <b>masuk</b>
                                    </li>
                                </ol>
                            </div>
                        </div>
                    </div>

                    <div className="bg-gray-100 px-4 py-3 ">
                        <button
                            onClick={(e) => setShow(prev=>!prev)}
                            type="button"
                            className="justify-center w-full rounded-md px-4 py-2 bg-red-500 font-medium text-white hover:bg-green-500"
                        >
                            Tutup
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}
export default ShowNotifikasi;
