import { Navbar } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
// import { navLinks } from "../Data/Data";
import ShowNotifikasi from "./showNotifikasi";
import { BsClock } from "react-icons/bs";
import {
    IoHome,
    IoCalendarNumberOutline,
    IoNotificationsOutline
} from "react-icons/io5";

import "../style/Componen/nav.css";

function NavbarComponen() {
    // Menampilkam Notifikasi
    const [show, setShow] = useState(false);

    // Membuay Nav timbul ketika di Scroll
    const [changeColor, setChangColor] = useState(false);
    const changeBacColor = () => {
        if (window.scrollY > 10) {
            setChangColor(true);
        } else {
            setChangColor(false);
        }
    };
    useEffect(() => {
        changeBacColor();
        window.addEventListener("scroll", changeBacColor);
    });
    // Show jam digital
    function jam() {
        setInterval(function () {
            var waktu = new Date();
            // var jam = document.getElementById("jam");

            var hours = waktu.getHours();
            var minutes = waktu.getMinutes();
            var seconds = waktu.getSeconds();

            if (waktu.getHours() < 10) {
                hours = "0" + waktu.getHours();
            }
            if (waktu.getMinutes() < 10) {
                minutes = "0" + waktu.getMinutes();
            }
            if (waktu.getSeconds() < 10) {
                seconds = "0" + waktu.getSeconds();
            }

        }, 1000);
            return `${hours}${minutes} ${seconds}`;
    }

    function showMasehi() {
        setInterval(function () {
            let tod = new Date();
            let weekday = [
                "Minggu",
                "Senin",
                "Selasa",
                "Rabu",
                "Kamis",
                "Jumat",
                "Sabtu"
            ];
            let monthname = [
                "Januari",
                "Februari",
                "Maret",
                "April",
                "Mei",
                "Juni",
                "Juli",
                "Agustus",
                "September",
                "Oktober",
                "November",
                "Desember"
            ];
            let y = tod.getFullYear();
            let m = tod.getMonth();
            let d = tod.getDate();
            let dow = tod.getDay();
            const tglMasehi =
                weekday[dow] + " " + d + " " + monthname[m] + " " + y + " M";
            document.getElementById("tglMasehi").innerHTML = tglMasehi;
        }, 1000);
    }
    showMasehi();

    function Hijriyah() {
        setInterval(function () {
            let fixd;
            function gregToFixed(year, month, day) {
                let a = Math.floor((year - 1) / 4);
                let b = Math.floor((year - 1) / 100);
                let c = Math.floor((year - 1) / 400);
                let d = Math.floor((367 * month - 362) / 12);

                if (month <= 2);
                return 1 - 1 + 365 * (year - 1) + a - b + c + d + day;
            }

            function Hijri(year, month, day) {
                this.year = year;
                this.month = month;
                this.day = day;
                this.toFixed = hijriToFixed;
                this.toString = hijriToString;
            }

            function hijriToFixed() {
                return (
                    this.day +
                    Math.ceil(29.5 * (this.month - 1)) +
                    (this.year - 1) * 354 +
                    Math.floor((3 + 11 * this.year) / 30) +
                    227015 -
                    1
                );
            }

            function hijriToString() {
                let months = [
                    "Muharram",
                    "Safar",
                    "Rabiul Awwal",
                    "Rabiul Tsani",
                    "Jumadil Ula",
                    "Jumadil Tsani",
                    "Rajab",
                    "Sya'ban",
                    "Ramadhan",
                    "Syawwal",
                    "Dzul Qa'dah",
                    "Dzul Hijjah"
                ];
                return (
                    this.day + " " + months[this.month - 1] + " " + this.year
                );
            }

            function fixedToHijri(f) {
                let i = new Hijri(1100, 1, 1);
                i.year = Math.floor((30 * (f - 227015) + 10646) / 10631);
                let i2 = new Hijri(i.year, 1, 1);
                let m = Math.ceil((f - 29 - i2.toFixed()) / 29.5) + 1;
                i.month = Math.min(m, 12);
                i2.year = i.year;
                i2.month = i.month;
                i2.day = 1;
                i.day = f - i2.toFixed() + 1;
                return i;
            }
            let tod = new Date();
            let y = tod.getFullYear();
            let m = tod.getMonth();
            let d = tod.getDate();

            m++;
            fixd = gregToFixed(y, m, d);
            let h = new Hijri(1421, 11, 28);
            h = fixedToHijri(fixd);

            let tglHijriyah = h.toString() + " H";
            document.getElementById("tglHijriyah").innerHTML = tglHijriyah;
        }, 10);
    }
    Hijriyah();
    // End Show Jam Digital

    return (
        <div className="">
            <div>{show && <ShowNotifikasi />}</div>
            <Navbar className={changeColor ? "color-active" : ""}>
                <div className="w-screen p-3">
                    <div className="flex text-rose-700 justify-between items-center">
                        <div className="justify-center">
                            <Link
                                to="/"
                                target="_blank"
                                className="flex flex-row items-baseline font-bold  gap-x-1 text-[25px]"
                            >
                                <IoHome />
                                cPhone
                            </Link>
                        </div>

                        <button
                            onClick={() => setShow(!show)}
                            type="button"
                            className="relative inline-block mr-2 text-[30px]"
                        >
                            <IoNotificationsOutline />
                            <div className="absolute top-2 right-2 inline-flex items-center justify-center px-2 py-1 text-xs font-bold leading-none transform translate-x-1/2 -translate-y-1/2 bg-green-600 rounded-full w-4 h-4">
                                2
                            </div>
                        </button>
                    </div>
                    <div className="text-rose-700 font-bold flex items-center">
                        <p className="text-[25px]">
                            <IoCalendarNumberOutline />
                        </p>
                        <div className="flex-1 px-1 flex-row text-start">
                            <div className="border-b border-red-600">
                                <p id="tglMasehi">tglMasehi</p>
                            </div>
                            <p id="tglHijriyah">tglHijriyah</p>
                        </div>
                        <div className="flex items-center gap-1.5">
                            <BsClock />
                            <p id="jam">{jam()}</p>
                        </div>
                    </div>
                </div>
            </Navbar>
        </div>
    );
}
export default NavbarComponen;
