import { useState } from "react";
import { Container } from "react-bootstrap";

function Bolean(argument) {
    // inisialisasi state dengan ttpe data Boolean
    const [isOn, setIsOn] = useState(false);

    // fungsi untuk mengetahui nilai Bolean state
    const toggleSwich = () => {
        setIsOn(!isOn); // mengubah Bolean mnjadi sebaliknya
    };
    return (
        <Container>
            <h2>Toggle Button</h2>
            <p>Apakah tombil ini Berfungsu ? {isOn ? "Yes" : "No"}</p>
            <button onClick={toggleSwich}>Toggle</button>
        </Container>
    );
}
export default Bolean;
