import { useState } from "react";
import { Container, Alert } from "react-bootstrap";

function TextInput(argument) {
    // inisialisasi state dgn tipe data String
    const [text, setText] = useState("");

    // fungsi mengubah nilai String
    const handleChange = e => {
        setText(e.target.value); // mengubah nilai String sesuai input Tesk
    };
    return (
        <Container>
            <h1>Input Teks</h1>
            <input type="text" value={text} onChange={handleChange} />
            <p>memasukan = {text}</p>
            <Alert>haloo</Alert>
        </Container>
    );
}
export default TextInput;
