import React, { useEffect, useState } from "react";
// Api
import { getUpcomingMovies } from "../../Api.js";
// Components
import MovieCard from "../MovieCard";
import MovieSkeleton from "../MovieSkeleton";

const MovieUpcoming = () => {
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);


    useEffect(() => {
        const fetchMovies = async () => {
            const response = await getUpcomingMovies();
            setMovies(response);
            setIsLoading(false);
        };

        fetchMovies();
    }, []);



    return (
        <div>
            <div className="flex justify-between mb-4 mt-9">
                <h2 className="text-xl font-primary">UpComing</h2>

            </div>
            <div className="flex space-x-4 snap-x overflow-x-auto scroll-smooth">
                {isLoading
                    ? Array.from({ length: 10 }).map((_, index) => (
                          <MovieSkeleton key={index} />
                      ))
                    : movies.map(movie => {
                          return (
                              <div
                                  key={movie.id}
                                  className="min-w-[155px] md:min-w-[170px] lg:min-w-[180px] flex flex-col"
                              >
                                  <MovieCard key={movie.id} movie={movie} />
                              </div>
                          );
                      })}
            </div>
        </div>
    );
};

export default MovieUpcoming;
