// Home
import React, { useState, useEffect } from "react";
// Api.js
import { searchMovie, getPopularMovies } from "../Api.js";
// NavbarDate
import NavbarDate from "../components/NavbarDate/NavbarDate";
//components
import MovieTrending from "../components/MoviesSlider/MovieTrending.jsx";
import MoviePopular from "../components/MoviesSlider/MoviePopular.jsx";
import MovieUpcoming from "../components/MoviesSlider/MovieUpcoming.jsx";
import SearchMovie from "../components/SearchMovie.jsx";

//utils
import ButtonUp from "../utils/ButtonUp";

//
function Home() {
    const [popularMovies, setPopularMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        getPopularMovies().then(result => {
            setPopularMovies(result);
            setIsLoading(false);
        });
    }, []);

    const search = async q => {
        setIsLoading(true);
        const query = await searchMovie(q);
        setPopularMovies(query.results);
        setIsLoading(false);
    };


    return (
        <main id="home" className="bg-site bg-cover overflow-hidden">
            <NavbarDate />
            <div className="container mx-auto mt-40">
                <SearchMovie />
                {isLoading ? (
                    <p>Loading...</p>
                ) : (
                    <MovieUpcoming movies={movies} />
                )}

                {isLoading ? (
                    <p>Loading...</p>
                ) : (
                    <MovieTrending movies={movies} />
                )}
                {isLoading ? (
                    <p>Loading...</p>
                ) : (
                    <MoviePopular movies={movies} />
                )}
            </div>
            <ButtonUp />
        </main>
    );
}
export default Home;
