// src/components/SearchMovies.jsx

import React, { useState } from "react";
import { searchMovie } from "../Api"; // pastikan import yang benar
import MovieCard from "./MovieCard"; // pastikan import yang benar

const SearchMovies = () => {
    const [query, setQuery] = useState("");
    const [results, setResults] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const handleSearch = async () => {
        setIsLoading(true);
        try {
            const data = await searchMovie(query);
            setResults(data.results);
            setIsModalOpen(true);
        } catch (error) {
            console.error("Error fetching search results:", error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleChange = e => {
        setQuery(e.target.value);
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="relative container items-center flex flex-col">
            <input
                type="text"
                value={query}
                onChange={handleChange}
                placeholder="Cari filem kesayangan..."
                className="w-[70%] h-[30px] p-2 my-4 rounded-full border font-bold text-black" // Pastikan warna teks terlihat
            />
            <button onClick={handleSearch} className="btn text-sm p-2">
                Search
            </button>

            {isModalOpen && (
                <div
                    className="fixed z-50 overflow-hidden overflow-y-auto top-0 w-full left-0 h-screen"
                    id="notifikasi"
                >
                    <div className="fixed inset-x-0 px-4 pb-4 inset-0 flex z-50 overflow-hidden items-center justify-center backdrop-blur bg-black/10">
                        <div className="bg-white/60 pt-4 border-2 backdrop-blur rounded-lg overflow-y-hidden w-full max-w-4xl">
                            <div className="flex items-center flex-col overflow-y-scroll">
                                <div className="mt-8 p-8 text-black text-left">
                                    {isLoading ? (
                                        <p>Loading...</p>
                                    ) : (
                                        <div className="grid grid-cols-3 gap-4">
                                            {results.map(movie => (
                                                <MovieCard
                                                    key={movie.id}
                                                    movie={movie}
                                                />
                                            ))}
                                        </div>
                                    )}
                                </div>
                            </div>

                            <div className="bg-gray-100 px-4 py-3">
                                <button
                                    onClick={handleCloseModal}
                                    type="button"
                                    className="btn justify-center w-full rounded-md px-4 py-2"
                                >
                                    Tutup
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default SearchMovies;
