// components/MovieTrending.jsx
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTrending } from "../../Api";
import { setMovies, setIsLoading } from "../../stores/MovieSlice";
import Toggle from "../Toggle";
import MovieCard from "../MovieCard";
import MovieSkeleton from "../MovieSkeleton";

const MovieTrending = () => {
    const dispatch = useDispatch();
    const movies = useSelector(state => state.movies.movies);
    const isLoading = useSelector(state => state.movies.isLoading);
    const [toggleActive, setToggleActive] = useState("today");

    const toggles = [
        { id: "today", label: "Today" },
        { id: "week", label: "This Week" }
    ];

    const fetchMovies = async timeWindow => {
        dispatch(setIsLoading(true));
        const response = await getTrending(timeWindow);
        dispatch(setMovies(response));
        dispatch(setIsLoading(false));
    };

    useEffect(() => {
        fetchMovies(toggleActive === "today" ? "day" : "week");
    }, [toggleActive]);

    const handleChangeToggle = value => {
        setToggleActive(value);
        fetchMovies(value === "today" ? "day" : "week");
    };

    return (
        <div>
            <div className="flex justify-between mb-4 mt-9">
                <h2 className="text-xl font-primary">Trending</h2>
                <Toggle
                    toggles={toggles}
                    toggleActive={toggleActive}
                    handleChangeToggle={handleChangeToggle}
                />
            </div>
            <div className="flex space-x-4 snap-x overflow-x-auto scroll-smooth">
                {isLoading
                    ? Array.from({ length: 10 }).map((_, index) => (
                          <MovieSkeleton key={index} />
                      ))
                    : movies.map(movie => (
                          <div
                              key={movie.id}
                              className="min-w-[155px] md:min-w-[170px] lg:min-w-[180px] flex flex-col"
                          >
                              <MovieCard key={movie.id} movie={movie} />
                          </div>
                      ))}
            </div>
        </div>
    );
};

export default MovieTrending;
