import React from "react";
import { Container } from "react-bootstrap";
import "./MovieList.css";
const SkeletonLoader = () => {
    return (
        <div className="skeleton bg-white/20">
            <Container className="grid grid-rows-* mx-auto">
                <div className="text-center bg-gray-300 rounded-2xl border-2 border-gray-200 m-1 overflow-hidden animate-pulse">
                    <div className="h-48 bg-gray-400"></div>
                    <div className="m-4">
                        <div className="h-6 bg-gray-400 mb-2"></div>
                        <div className="h-4 bg-gray-400 mb-1"></div>
                        <div className="h-4 bg-gray-400"></div>
                    </div>
                </div>
            </Container>
        </div>
    );
};

const MoviesList = ({ popularMovies, isLoading }) => {
    if (isLoading) {
        return (
            <div className="movies-grid space-x-1">
                {Array.from({ length: 10 }).map((_, index) => (
                    <SkeletonLoader key={index} />
                ))}
            </div>
        );
    }

    return (
        <div className="movies-grid">
            {popularMovies.map((movie, i) => (
                <div className="bg-white/20" key={i}>
                    <Container className="my-4 mx-auto">
                        <div className="text-center bg-amber-400 rounded-2xl border-2 border-white m-1 overflow-hidden">
                            <div className="group relative overflow-hidden justify-items-center items-center">
                                <div className="group-hover:bg-black/70 w-full h-full absolute z-10 transition-all duration-700"></div>
                                <div className="lg:flex lg:justify-items-center lg:items-center lg:text-center">
                                    <img
                                        src={`${process.env.REACT_APP_IMGURL}/${movie.poster_path}`}
                                        alt={movie.title}
                                        className="movie-poster group-hover:scale-150 transition-all duration-700"
                                    />
                                    <span className="font-black text-[15px] text-ellipsis line-clamp-1">
                                        {movie.title}
                                    </span>
                                </div>
                            </div>
                        </div>
                    </Container>
                </div>
            ))}
        </div>
    );
};

export default MoviesList;
